INSERT INTO `concierto` (id, tipo, lugar, fecha, noticia, programa) VALUES (22, 'Espectáculo poético musical', 'Teatro Barakaldo', '2009-05-25 20:00:00', 'Espectáculo poético musical en el Teatro Barakaldo', '<b>ESPECTÁCULO POÉTICO MUSICAL: TIENEN LA PALABRA<br /><br />\r\n UN RECORRIDO POR LA POESIA VASCA<br />\r\nMÚSICA, DANZA Y POEMAS<br />\r\n</b>\r\n<br /><br />\r\n<b>TIENEN LA PALABRA</b>\r\n<br /><br />\r\n1º El Coro y Orquesta interpreta <b>SCARBOROUGH FAIR</b><br /><br />\r\n2º El Coro y Orquesta interpretan <b>DRAVIDIAN DYTHIRAMB</b>, los rapsodas recitan el recitativo de Ángela Figueroa y a su vez la bailarina Nuria baila con los compases de la orquesta.<br /><br />\r\n\r\n3º Los tres Rapsodas:\r\n \r\n<ul class="cuadrado">\r\n<li>Antonio Trueba (La doncella de Bermeo)</li>\r\n<li>Miguel Unamuno (El cuerpo canta- leer, leer, leer)</li>\r\n<li>Ernestina de Champourcin (Aún hay calles)</li>\r\n<li>Gabriel Celaya (La poesía es un arma...)</li>\r\n<li>Blas de Otero (La Tierra)</li>\r\n</ul>\r\n<br />\r\n4º El coro y orquesta y interpreta Maitia nun Zira<br /><br />\r\n5º Los tres Rapsodas:\r\n\r\n<ul class="cuadrado">\r\n<li>Javier de Bengoetxea (Cromos)</li>\r\n<li>Eusebio Abasolo (Rió)</li>\r\n<li>Bitoriano Gandiaga (Varios) euskera y castellano</li>\r\n<li>Teresa Aldamiz (Melancolías)</li>\r\n<li>Gabriel Aresti (Nerea-Defenderé la casa de mi padre) Eusk-Castellano</li>\r\n<li>Blanca Sarasua (Todo es reciclable)</li>\r\n<li>Amalia Lasa (No soy una Flor) Euskera-Castellano</li>\r\n</ul><br />\r\n6º El Coro y Orquesta interpretará:\r\n\r\n<ul class="cuadrado">\r\n<li>Ave Mari (Mascagni)</li>\r\n<li>Swing Low, Sweet Chariot</li>\r\n</ul><br />  \r\n7º Los tres Rapsodas:\r\n<ul class="cuadrado">\r\n<li>Mari Maizkurrena (El Mundo)</li>\r\n<li>J. Fernández de la Sota (Una anchuroso y calida morada)</li> \r\n<li>P. González de Langarika (Un pájaro pasó)</li>\r\n<li>Bernardo Atxaga (Las Gaviotas)</li>\r\n<li>Carlos Aurtenetxe (Vengo del Viento)</li>\r\n\r\n<li>Carmen Bereciartua (Moneda de Cambio)</li>\r\n<li>Karlos Linazasoro (Poetika) Euskera, Castellano</li>\r\n</ul><br />\r\n\r\n8º Coro y Orquesta: <b>Aita Gurea</b> <br /><br />\r\n9º Los tres rapsodas:\r\n<ul class="cuadrado">\r\n<li>Pedro Ugarte (Memorial de agravios)</li>\r\n<li>Harkait Kano (El mar comunista)</li>\r\n<li>Javier Viar (Bolero)</li>\r\n\r\n<li>Kirmen Uribe (No puedo elegir Mayo) Eusk-Cast</li>\r\n<li>Miren Agur Meabe (Código) Eusk-Castellano</li>\r\n</ul><br />\r\n\r\n10º El  Coro y Orquesta: <b>Gloria</b><br /><br />\r\n\r\n11º Se  interpretará <b>La Muerte de Asse</b> y saliendo la bailarina a danzar la obra con la voz de los Rapsodas en el Juicio Final de Blas de Otero.<br /><br /><br /><br />\r\n\r\n<b>REPRESENTACIÓN A CARGO DE:</b>\r\n\r\n<br /><br />\r\nOrquesta y Coro de Cámara de Bilbao (director Txaber Fernández Martín)\r\n<br /><br />\r\nRapsodas: Marina Pérez, Loly Rubio y Miguel Cuadrado\r\n<br /><br />\r\nBailarina: Nuria Pérez\r\n<br /><br />\r\nAudiovisuales: Miguel Cuadrado  \r\n<br /><br />\r\nRealización: Txema Herrero (Coro y Orquesta de Cámara de Bilbao)\r\n<br /><br />');
